import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const initialForm = {
  name: '',
  email: '',
  phone: '',
  subject: 'Support',
  message: '',
  contactMethod: 'Email',
  bestTime: '',
  agree: false,
};

export default function Contact() {
  const [form, setForm] = useState(initialForm);
  const [touchedSubmit, setTouchedSubmit] = useState(false);
  const nameRef = useRef(null);
  const emailRef = useRef(null);
  const phoneRef = useRef(null);
  const messageRef = useRef(null);
  const agreeRef = useRef(null);
  const navigate = useNavigate();

  function validate(values) {
    const e = {};
    if (!values.name || values.name.trim().length < 2) e.name = 'Name required (min 2 chars)';
    if (values.name && values.name.length > 50) e.name = 'Name must be at most 50 chars';
    if (!values.email) e.email = 'Email required';
    else {
      const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!re.test(values.email)) e.email = 'Invalid email format';
    }
    if (values.phone) {
      const digits = values.phone.replace(/\D/g, '');
      if (digits.length !== 10) e.phone = 'Phone must be 10 digits';
    }
    if (!values.message || values.message.trim().length < 20) e.message = 'Message must be at least 20 characters';
    if (!values.agree) e.agree = 'You must agree to terms';
    return e;
  }

  const currentErrors = validate(form);
  const isValid = Object.keys(currentErrors).length === 0;

  function handleChange(e) {
    const { name, value, type, checked } = e.target;
    setForm(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  }

  function focusFirstInvalid(errs) {
    if (errs.name) return nameRef.current?.focus();
    if (errs.email) return emailRef.current?.focus();
    if (errs.phone) return phoneRef.current?.focus();
    if (errs.message) return messageRef.current?.focus();
    if (errs.agree) return agreeRef.current?.focus();
  }

  function onSubmit(e) {
    e.preventDefault();
    setTouchedSubmit(true);
    const errs = validate(form);
    if (Object.keys(errs).length > 0) {
      focusFirstInvalid(errs);
      return;
    }

    const name = form.name;
    setForm(initialForm);
    // show success banner briefly using history state via navigate
    navigate('/thank-you', { state: { name } });
  }

  return (
    <section style={{ maxWidth: 700 }}>
      <h1>Contact (Controlled Form)</h1>
      <form onSubmit={onSubmit} noValidate>
        <div>
          <label>Full Name *</label><br />
          <input ref={nameRef} name='name' value={form.name} onChange={handleChange} />
          {touchedSubmit && currentErrors.name && <div style={{ color: 'red' }}>{currentErrors.name}</div>}
        </div>

        <div>
          <label>Email *</label><br />
          <input ref={emailRef} name='email' value={form.email} onChange={handleChange} type='email' />
          {touchedSubmit && currentErrors.email && <div style={{ color: 'red' }}>{currentErrors.email}</div>}
        </div>

        <div>
          <label>Phone (optional)</label><br />
          <input ref={phoneRef} name='phone' value={form.phone} onChange={handleChange} placeholder='1234567890 or (123) 456-7890' />
          {touchedSubmit && currentErrors.phone && <div style={{ color: 'red' }}>{currentErrors.phone}</div>}
        </div>

        <div>
          <label>Subject</label><br />
          <select name='subject' value={form.subject} onChange={handleChange}>
            <option>Support</option><option>Sales</option><option>Feedback</option><option>Other</option>
          </select>
        </div>

        <div>
          <label>Message *</label><br />
          <textarea ref={messageRef} name='message' value={form.message} onChange={handleChange} rows={6} />
          {touchedSubmit && currentErrors.message && <div style={{ color: 'red' }}>{currentErrors.message}</div>}
        </div>

        <div>
          <label>Preferred Contact Method</label><br />
          <label><input type='radio' name='contactMethod' value='Email' checked={form.contactMethod === 'Email'} onChange={handleChange} /> Email</label>{' '}
          <label><input type='radio' name='contactMethod' value='Phone' checked={form.contactMethod === 'Phone'} onChange={handleChange} /> Phone</label>
        </div>

        <div>
          <label>Best Time to Reach (optional)</label><br />
          <input type='time' name='bestTime' value={form.bestTime} onChange={handleChange} />
        </div>

        <div>
          <label><input ref={agreeRef} type='checkbox' name='agree' checked={form.agree} onChange={handleChange} /> I agree to terms *</label>
          {touchedSubmit && currentErrors.agree && <div style={{ color: 'red' }}>{currentErrors.agree}</div>}
        </div>

        <div style={{ marginTop: 12 }}>
          <button type='submit' disabled={!isValid}>Submit</button>
          {!isValid && <span style={{ marginLeft: 10, color: '#a00' }}>Complete required fields to enable submit</span>}
        </div>
      </form>
    </section>
  );
}
